var movieGenres = Object.keys(movieList);

export default function App() {
  const [genre, setGenre] = useState("action");

  function clickHandler(newGenre) {
    setGenre(newGenre);
    console.log(genre);
  }

  return (
    <div className="App">
      <nav>
        <h1>Moviegasm</h1>
      </nav>
      <main className="main">
        <div className="genreList">
          {movieGenres.map((genre) => {
            return (
              <span
                className="genreButton"
                key={genre}
                onClick={() => clickHandler(genre)}
              >
                {genre}
              </span>
            );
          })}
        </div>

        <div className="movieList">
          {movieList[genre].map((movie, index) => {
            return (
              <div className="listMovie" key={index}>
                <img
                  src={movie.image}
                  alt="Movie"
                  className="movie-image"
                ></img>
                <div className="movie-details">
                  <h3 className="movie-name">{movie.name}</h3>
                  <h4 className="runtime">{movie.runtime}</h4>
                  <p className="summary">{movie.summary}</p>
                  <h4>
                    Director:{" "}
                    <span className="director-name">{movie.director}</span>
                  </h4>
                  <h4 className="imdb">
                    IMDB: <span className="rating">{movie.rating}</span>{" "}
                  </h4>
                </div>
              </div>
            );
          })}
        </div>
      </main>

      <footer>
        <p>&copy; Satyaki | 2020 </p>
      </footer>
    </div>
  );
}
